#!/usr/bin/env python3
# -*- coding: utf-8 -*-
import json

with open('nobider_jiboni.json', 'r', encoding='utf-8') as f:
    data = json.load(f)

for nabi in data:
    if nabi['id'] == 'nabi_isa_as':
        nabi['paragraphs'] = [
            "হযরত ঈসা (আঃ) ছিলেন মারইয়াম (আঃ) এর পুত্র এবং পিতা ছাড়াই জন্মগ্রহণ করেছিলেন। তিনি ছিলেন আল্লাহর মহান নবী এবং 'রুহুল্লাহ' (আল্লাহর রূহ) ও 'মাসিহ' উপাধিতে ভূষিত। আল্লাহ তাঁকে ইনজিল কিতাব দিয়েছিলেন এবং অসংখ্য মুজিজা দান করেছিলেন।",
            "**মারইয়াম (আঃ) এর পবিত্রতা:** মারইয়াম (আঃ) ছিলেন ইমরান (আঃ) এর কন্যা এবং অত্যন্ত পবিত্র মহিলা। তিনি বাইতুল মাকদিসে ইবাদত করতেন এবং যাকারিয়া (আঃ) তাঁর অভিভাবক ছিলেন। যাকারিয়া (আঃ) যখনই তাঁর কাছে যেতেন তখন তাঁর কাছে খাবার পেতেন। তিনি জিজ্ঞাসা করলেন: 'হে মারইয়াম! এটা তোমার কাছে কোথা থেকে এলো?' মারইয়াম (আঃ) বললেন: 'এটা আল্লাহর পক্ষ থেকে। নিশ্চয়ই আল্লাহ যাকে চান বিনা হিসাবে রিজিক দেন।'",
            "**ঈসা (আঃ) এর সুসংবাদ:** একদিন মারইয়াম (আঃ) ইবাদতের জন্য একাকী ছিলেন। জিবরাঈল (আঃ) মানুষের রূপে তাঁর কাছে এলেন। মারইয়াম (আঃ) ভয় পেয়ে বললেন: 'আমি তোমার থেকে দয়াময়ের আশ্রয় চাই, যদি তুমি আল্লাহভীরু হও।' জিবরাঈল (আঃ) বললেন: 'আমি তোমার রবের পক্ষ থেকে প্রেরিত। আমি তোমাকে একটি পবিত্র পুত্র দেব।' মারইয়াম (আঃ) বললেন: 'কীভাবে আমার পুত্র হবে যখন কোনো মানুষ আমাকে স্পর্শ করেনি এবং আমি ব্যভিচারিণীও নই?' জিবরাঈল (আঃ) বললেন: 'এভাবেই হবে। তোমার রব বলেছেন: এটা আমার জন্য সহজ। আমি তাকে মানুষের জন্য নিদর্শন এবং আমার পক্ষ থেকে রহমত বানাব।' জিবরাঈল (আঃ) মারইয়াম (আঃ) এর দিকে ফুঁ দিলেন এবং তিনি গর্ভবতী হলেন।",
            "**ঈসা (আঃ) এর জন্ম:** মারইয়াম (আঃ) গর্ভবতী হওয়ার পর মানুষের ভয়ে দূরে চলে গেলেন। একটি খেজুর গাছের নিচে তাঁর প্রসব বেদনা শুরু হলো। তিনি কষ্টে বললেন: 'হায়! আমি যদি এর আগে মরে যেতাম এবং সম্পূর্ণ বিস্মৃত হয়ে যেতাম!' তখন ঈসা (আঃ) তাঁকে ডেকে বললেন: 'দুঃখ করো না। তোমার রব তোমার নিচে একটি ঝর্ণা সৃষ্টি করেছেন। খেজুর গাছ নাড়া দাও, তোমার উপর তাজা খেজুর পড়বে। খাও, পান করো এবং চোখ জুড়াও।'",
            "**শিশু ঈসা (আঃ) এর কথা বলা:** মারইয়াম (আঃ) শিশু নিয়ে তাঁর জাতির কাছে ফিরে এলেন। তারা বলল: 'হে মারইয়াম! তুমি তো অদ্ভুত কাজ করেছ! তোমার পিতা খারাপ মানুষ ছিলেন না এবং তোমার মা ব্যভিচারিণী ছিলেন না!' মারইয়াম (আঃ) শিশুর দিকে ইশারা করলেন। তারা বলল: 'আমরা কীভাবে এমন একজনের সাথে কথা বলব যে দোলনায় শিশু?' তখন ঈসা (আঃ) কথা বললেন:",
            "إِنِّي عَبْدُ اللَّهِ آتَانِيَ الْكِتَابَ وَجَعَلَنِي نَبِيًّا ۝ وَجَعَلَنِي مُبَارَكًا أَيْنَ مَا كُنتُ وَأَوْصَانِي بِالصَّلَاةِ وَالزَّكَاةِ مَا دُمْتُ حَيًّا",
            "অর্থ: আমি আল্লাহর বান্দা। তিনি আমাকে কিতাব দিয়েছেন এবং আমাকে নবী বানিয়েছেন। তিনি আমাকে বরকতময় করেছেন যেখানেই আমি থাকি এবং আমাকে নামাজ ও যাকাতের নির্দেশ দিয়েছেন যতদিন আমি জীবিত থাকব। [সূরা মারইয়াম: ৩০-৩১]",
            "**মুজিজাসমূহ:** ঈসা (আঃ) বড় হয়ে নবুয়ত পেলেন এবং বনী ইসরাঈলের কাছে দাওয়াত দিতে শুরু করলেন। আল্লাহ তাঁকে অনেক মুজিজা দিয়েছিলেন। তিনি মাটি দিয়ে পাখির আকৃতি বানাতেন এবং তাতে ফুঁ দিলে আল্লাহর হুকুমে তা জীবিত পাখি হয়ে উড়ে যেত। তিনি জন্মান্ধকে দৃষ্টি দিতেন এবং কুষ্ঠ রোগীকে সুস্থ করতেন। তিনি মৃতকে জীবিত করতেন আল্লাহর অনুমতিতে। তিনি মানুষকে বলতেন তারা ঘরে কী খায় এবং কী জমা করে রাখে।",
            "**হাওয়ারিগণ:** ঈসা (আঃ) এর বারোজন বিশেষ সাহাবী ছিলেন যাদের হাওয়ারি বলা হতো। তারা তাঁর প্রতি ঈমান এনেছিল এবং তাঁকে সাহায্য করত। একবার তারা বলল: 'হে ঈসা! আপনার রব কি আমাদের জন্য আসমান থেকে খাবারের দস্তরখান নামাতে পারেন?' ঈসা (আঃ) বললেন: 'আল্লাহকে ভয় করো যদি তোমরা মুমিন হও।' তারা বলল: 'আমরা তা থেকে খেতে চাই এবং আমাদের অন্তর প্রশান্ত হবে।' ঈসা (আঃ) দোয়া করলেন এবং আল্লাহ আসমান থেকে খাবারের দস্তরখান নামালেন।",
            "**ইহুদিদের ষড়যন্ত্র ও আসমানে উঠানো:** ইহুদিরা ঈসা (আঃ) কে মানল না এবং তাঁকে হত্যা করার ষড়যন্ত্র করল। তারা রোমান শাসকদের কাছে মিথ্যা অভিযোগ করল। কিন্তু আল্লাহ তাঁকে রক্ষা করলেন। আল্লাহ বলেছেন:",
            "وَمَا قَتَلُوهُ وَمَا صَلَبُوهُ وَلَٰكِن شُبِّهَ لَهُمْ",
            "অর্থ: তারা তাঁকে হত্যা করেনি এবং শূলেও চড়ায়নি, বরং তাদের জন্য সাদৃশ্য করা হয়েছিল। [সূরা নিসা: ১৫৭]",
            "আল্লাহ ঈসা (আঃ) কে আসমানে তুলে নিলেন এবং অন্য একজনকে তাঁর মতো বানিয়ে দিলেন। ইহুদিরা সেই ব্যক্তিকে হত্যা করল ভেবে যে সে ঈসা (আঃ)। ঈসা (আঃ) এখনও জীবিত আছেন এবং কিয়ামতের আগে পৃথিবীতে ফিরে আসবেন, দাজ্জালকে হত্যা করবেন এবং ন্যায় প্রতিষ্ঠা করবেন।"
        ]

